import React from 'react';
import {
  StyleSheet,
  Text,
  View,
  TextInput,
  KeyboardAvoidingView,
  TouchableOpacity,
  Alert,
  Button,
} from 'react-native';
import * as ImagePicker from 'expo-image-picker';

import db from '../config';
import MyHeader from '../components/header';
import firebase from 'firebase';
import { SafeAreaProvider, SafeAreaView } from 'react-native-safe-area-context';
import { Avatar } from 'react-native-elements';
import { LinearGradient } from 'expo-linear-gradient';

export default class CreateEvent extends React.Component {
  constructor() {
    super();
    this.state = {
      userID: firebase.auth().currentUser.email,
      title: '',
      description: '',
      time: '',
      address: '',
      city: '',
      image: 'data:image/png;base64,',
    };
  }

  selectPicture = async () => {
    const { cancelled, uri } = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.All,
      allowsEditing: true,
      aspect: [4, 3],
      quality: 1,
    });

    if (!cancelled) {
      this.setState({ image: uri });
    }
  };

  uploadImage = async (uri, imageName) => {
    var response = await fetch(uri);
    var blob = await response.blob();

    var ref = firebase
      .storage()
      .ref()
      .child('event_details/' + imageName);

    return ref.put(blob).then((response) => {
      console.log('upload successful');
      this.fetchImage(imageName);
    });
  };

  fetchImage = (imageName) => {
    var storageRef = firebase
      .storage()
      .ref()
      .child('event_details/' + imageName);

    // Get the download URL
    storageRef
      .getDownloadURL()
      .then((url) => {
        this.setState({ image: url });
      })
      .catch((error) => {
        this.setState({ image: '#' });
      });
  };
  createUniqueId() {
    return Math.random().toString(36).substring(7);
  }
  addEvent = (title, time, request, uri, address, city) => {
    var userID = this.state.userID;
    var ItemId = this.createUniqueId();

    console.log('log 2');
    this.uploadImage(this.state.image, ItemId);
    db.collection('invites').add({
      userId: userID,
      invite: title,
      image: uri,
      description: request,
      address: address,
      city: city,
      time: time,
      ID: ItemId,
      status: 'open',
      participants: 0,
    });
    console.log('database works');
    this.setState({
      title: '',
      time: '',
      description: '',
      city: '',
      address: '',
    });
    return Alert.alert('Event Added');
  };
  render() {
    return (
      <SafeAreaProvider style={{ flex: 1, backgroundColor: 'white' }}>
        <MyHeader
          title="Add Event"
          navigation={this.props.navigation}></MyHeader>
        <KeyboardAvoidingView style={styles.keyView}>
          <View
            style={{
              width: '100%',
              flexDirection: 'row',
              alignItems: 'right',
              marginTop: 20,
              marginLeft: 10,
            }}>
            <Avatar
              source={{
                uri: this.state.image,
              }}
              size={'xlarge'}
              onPress={() => this.selectPicture()}
              showEditButton
            />
            <View
              style={{
                flexDirection: 'column',
                alignItems: 'center',
                marginLeft: 10,
              }}>
              <TextInput
                style={styles.input2}
                placeholder={'Title'}
                onChangeText={(text) => {
                  this.setState({
                    title: text,
                  });
                }}
                value={this.state.title}></TextInput>
              <TextInput
                style={styles.input}
                placeholder={'Date and Time'}
                onChangeText={(text) => {
                  this.setState({
                    time: text,
                  });
                }}
                value={this.state.time}></TextInput>
              <TextInput
                style={styles.input}
                placeholder={'Description'}
                onChangeText={(text) => {
                  this.setState({
                    description: text,
                  });
                }}
                value={this.state.description}></TextInput>
              <TextInput
                style={styles.input}
                placeholder={'City'}
                onChangeText={(text) => {
                  this.setState({
                    city: text,
                  });
                }}
                value={this.state.city}></TextInput>
            </View>
          </View>
          <TextInput
            style={{
              width: '90%',
              height: 30,
              marginTop: 10,
              alignSelf: 'center',
              borderColor: '#FC3973',
              fontSize: 12,
              padding: 10,
              borderWidth: 1,
              borderRadius: 10,
            }}
            placeholder={'Address'}
            onChangeText={(text) => {
              this.setState({
                address: text,
              });
            }}
            value={this.state.address}></TextInput>
          <LinearGradient
            // Button Linear Gradient
            colors={['#FC3973', '#FF7455']}
            start={{ x: -1, y: 0 }}
            end={{ x: 1, y: 0 }}
            style={[styles.button, { marginBottom: 10, marginTop: 10 }]}>
            <TouchableOpacity
              onPress={() => {
                this.addEvent(
                  this.state.title,
                  this.state.time,
                  this.state.description,
                  this.state.image,
                  this.state.address,
                  this.state.city
                );
              }}>
              <Text style={styles.buttonText}>Add Event</Text>
            </TouchableOpacity>
          </LinearGradient>
        </KeyboardAvoidingView>
      </SafeAreaProvider>
    );
  }
}

const styles = StyleSheet.create({
  keyView: {
    flex: 1,
    justifyContent:"center", 
    alignItems:"center"
  },
  button: {
    width: 200,
    height: 50,
    justifyContent: 'center',
    alignItems: 'center',
    alignSelf: 'center',
    backgroundColor: '#FC3973',
    borderRadius: 25,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 8 },
    shadowOpacity: 0.3,
    shadowRadius: 10.32,
    elevation: 16,
    marginTop: 20,
  },
  input: {
    width: '100%',
    height: 30,
    marginTop: 10,
    borderColor: '#FC3973',
    fontSize: 12,
    padding: 10,
    borderWidth: 1,
    borderRadius: 10,
  },
  input2: {
    width: '100%',
    height: 30,
    borderColor: '#FC3973',
    fontSize: 12,
    padding: 10,
    borderWidth: 1,
    borderRadius: 10,
  },
  buttonText: {
    color: 'white',
    fontSize: 20,
  },
});
